import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

export default function App() {
  const getLocation = () => {
    navigator.geolocation.getCurrentPosition((position) => {
      getData(position.coords.latitude, position.coords.longitude);
    })
  }

